        public bool SendVerifyEmail(string toEmail, string name, string body, string subject)
        {
            try
            {
                string url = $"http://localhost:3000/authentication/verify-gmail?token={body}";
                var message = new MailMessage()
                {
                    From = new MailAddress("datj.company@gmail.com", "DATJ DIAMOND"),
                    Subject = subject,
                    IsBodyHtml = true,
                    Body = GetVerifyContent(name, url),
                };
                message.To.Add(toEmail);
                var smtp = new SmtpClient("smtp.gmail.com")
                {
                    Port = 587,
                    Credentials = new NetworkCredential(
                        "datj.company@gmail.com",
                        "qdfh hxaz rkcp yrwh"
                    ),
                    EnableSsl = true,
                };
                smtp.Send(message);
                return true;
            }
            catch (Exception ex)
            {
                Console.WriteLine(ex);
                return false;
            }
        }

